mapper.update:
UPDATE MEMBER_INFO SET NAME=#{name}, GENDER = #{gender}, BIRTH = #{birth}
							, PHONENUMBER = #{phoneNumber}, EMAIL = #{email}
			       WHERE IDX = #{idx}

		UPDATE (SELECT * FROM MEMBER, MEMBER_INFO WHERE MEMBER.IDX = MEMBER_INFO.IDX)
           SET NAME = 0, GENDER = 0, BIRTH = 0, PHONE_NUMBER = 0, LICENSE = 0, EMAIL = 0
         WHERE USERNAME = #{username}
